import React from 'react';

const post = (props) => {
  return (
    <>
    <div className="form__createPost">
      <h1
        className="input__name"
        value={namePost}
        onChange={HandlePostNameChange}
        type="text"
        placeholder="Name post"
      />
      <div className="form__inputTextArea">
        <h1
          className="input__text"
          value={textPost}
          />
      </div>
      <div className="input__dateAuthor">
        <h1  className="date" > {props.dateTime} </h1>
        <h1 className="name"> {props.name} </h1>
      </div>
    </div>
    <Button
      onClick={() =>
        createPost(
          namePost,
          textPost,
          dateTime,
          name
          )
          
        }
        title="Create post"
        />
  </>
  );
};

export default post;
