import PostService, { Posts } from "../../action/note";
import React, { useEffect, useRef, useState } from "react";
import Post from "../posts/post";
import { useDispatch, useSelector } from "react-redux";

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const { current: a } = useRef(["a"]);
  useEffect(() => {
     Posts().then(
       function (value) {
        // console.log(value);
        const allPosts =  value;
        setPosts(allPosts);
      },
      [a]
    );
  });

  console.log(posts);
  if (!posts.length) {
    return (
      <>
        <h1 style={{ textAlign: "center" }}>Posts not created</h1>
      </>
    );
  }
  return (
    <>
      {posts.map((post) => {
        <Post post={post} />;
      })}
    </>
  );
};

export default PostList;
