  // }
  // fetchData();
  const response = Posts().response
  console.log(typeof(response))
  if (!Posts.length) {
    return (
      <>
        <h1 style={{ textAlign: "center" }}>Posts not found</h1>
      </>
    );
  }
  return (
    <div>
      {Posts.map((Note) => {
        <Post
          namePost={Note.namePost}
          textPost={Note.textPost}
          dateTime={Note.dateTime}
          name={Note.name}
        />;
      })}
    </div>
  );
};

export default PostList;
