import axios from 'axios'
import {
    useEffect,
    useState
} from 'react'
import {setPost} from "../reducers/noterReducer";



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/notes/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })

        // console.log(response)
    } catch (e) {
        console.log(e)
    }

}

export const posts = () => {
    return async () => {
        try {
            const [post, setPost] = useState([]);
            useEffect(() => {
                axios.get(`http://localhost:5000/api/notes/posts`).then(res => {
                    setPost(res.post);
                })
            })
            return post
        } catch (e) {
            console.log(e.response.data)
        }
    }
}
