import React from "react";
import "../posts/formPost.css"
import { Posts } from "../../action/note";
import { useEffect, useState } from "react";


const Post = async (props)  => {

  const [posts, setPosts] = useState();

  useEffect(() => {
    async function fetchData() {
      const posts = await Posts();
      setPosts(posts);
    }
    fetchData();
  }, [{}]);

  console.log(posts[1])


  return (
    <>
      <div className="form__createPost">
        <h1> {props.namePost} </h1>
        <div className="form__inputTextArea">
          <h1 className="input__text"  > {props.textPost} </h1>
        </div>
        <div className="input__dateAuthor">
          <h1 className="date"> {props.dateTime} </h1>
          <h1 className="name"> {props.name} </h1>
        </div>
      </div>
    </>
  );
};

export default Post;
