import PostService, { Posts } from "../../action/note";
import React, { useEffect } from "react";
import { useState } from "react";
import Post from "../posts/post";
import { useDispatch, useSelector } from "react-redux";

const PostList = () => {
  useEffect(() => {
    Posts().then(function (value) {
      // console.log(value);
      const posts = value;
      return value;
    }, []);
  });

  // console.log(posts)
  if (!value.length) {
    return (
      <>
        <h1 style={{ textAlign: "center" }}>Posts not created</h1>
      </>
    );
  }
  return (
    <>
      {posts.map((post) => {
        <Post post={post} />;
      })}
    </>
  );
};

export default PostList;
