import axios from 'axios'



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/notes/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })
        
        // console.log(response)
    } catch (e) {
        console.log(e)
    }

}
