import axios from 'axios'
import {
    useEffect,
    useState
} from 'react'



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/posts/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })

        alert("Note was created successfully")
    } catch (e) {
        alert(e.response.data.message)
    }

}

export const Posts = async () => {
    try {
        const response = await axios.get('http://localhost:5000/api/posts/all-posts');
        // console.log(response.data)
        return response.data
    } catch (error) {
        console.log(error);
    }
}

