import React from "react";
import "../posts/formPost.css"
import { Posts } from "../../action/note";
import { useEffect, useState } from "react";


const Post =  (props)  => {
  
  console.log(props)
  return (
    <>
      <div className="form__createPost">
        <h1> {props.post.namePost} </h1>
        <div className="form__inputTextArea">
          <h1 className="input__text"  > {props.post.textPost} </h1>
        </div>
        <div className="input__dateAuthor">
          <h1 className="date"> {props.post.dateTime} </h1>
          <h1 className="name"> {props.post.name} </h1>
        </div>
      </div>
    </>
  );
};

export default Post;
