import { useState, useEffect } from 'react';
function Clock(){
  const [date, setDate] = useState(new Date());
  
  function refreshClock() {
    setDate(new Date());
  }
  return (
    <span>
      {date.toLocaleTimeString()}
        <script>
            setTimeout(() => {
                refreshClock()
            }, 1000);
        </script>
    </span>
   
  );
}
export default Clock;
