import axios from 'axios'
import {
    useEffect,
    useState
} from 'react'



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/posts/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })

        // console.log(response)
    } catch (e) {
        console.log(e.response.data.message)
    }

}

export const Posts = () => {
    const [posts, setPosts] = useState([])
    async function fetchData() {
        try {
          const Posts = await axios.get('http://localhost:5000/api/posts/posts');
          setPosts(Posts)
          console.log(posts);
        } catch (error) {
          console.log(error);
        }
      }
      fetchData()
      return posts

