import axios from 'axios'



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/notes/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })
        
        // console.log(response)
    } catch (e) {
        console.log(e)
    }

}

export const posts =  () => {
    return async () => {
        try {
           const response = await axios.get(`http://localhost:5000/api/notes/posts`)
           console.log(response)
           return response.json()
        } catch (e) {
            // console.log(e.response.data)
            localStorage.removeItem('token')
        }
    }
}
