import PostService, { Posts } from "../../action/note";
import React, { useEffect } from "react";
import { useState } from "react";
import Post from "../posts/post";
import { useDispatch, useSelector } from "react-redux";

const PostList = () => {
  useEffect(() => {
    Posts().then(function (value) {
      console.log(value);

      if (value.length <= 0) {
        return (
          <>
            <h1 style={{ textAlign: "center" }}>Posts not created</h1>
          </>
        );
      }
      return (
        <>
        <TransitionGroup>
            {value.map((post, index) => 
            <CSSTransition
                    key={post.id}
                    timeout={500}> 
                <Post post = {post} />
            </CSSTransition>
          )}     
            </TransitionGroup>
          {value.map((post , index) => {
            <Post post = {post}/>;
          })}
        </>
      ); 
    });
  }, []);
};

export default PostList;
