import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import "../posts/formPost.css";

const FormPost = () => {
  const [namePost, setNamePost] = useState("");
  const [textPost, setTextPost] = useState("");
  const HandlePostChange = (event) => {
    setTextPost(event.target.value);
  };
  const [datePost, setDatePost] = useState(Date().slice(0, 24));
  useEffect(() => {
    setInterval(() => setDatePost(new Date()), 30000);
}, []);
  const currentUser = useSelector((state) => state.user.currentUser.email);
  const str_user = String(currentUser);
  const name = str_user.split("@")[0];

  return (
    <div className="form__createPost">
        <input
          className="input__name"
          value={namePost}
          setValue={setNamePost}
          type="text"
          placeholder="Name post"
        />
      <div className="input__text">
        <textarea
          value={textPost}
          onChange={HandlePostChange}
          required="true"
          rows="10"
          cols="60"
          placeholder="Text post"
          maxlength="200"
        />
      </div>
      <div className="input__dateAuthor">
        <h1>{datePost}</h1>
        <h1> {name} </h1>
      </div>
    </div>
  );
};

export default FormPost;
