import axios from 'axios'
import {
    setNote
} from "../reducers/noteReducer";



export const createPost = async (namePost, textPost, dateTime, author) => {
    try {
        const response = await axios.post(`http://localhost:5000/api/notes/create-post`, {
            namePost,
            textPost,
            dateTime,
            author
        })
        alert(response.data.message)
    } catch (e) {
        alert(e.response.data.message)
    }

}
