import React from "react";
import "../posts/formPost.css";
import { Posts } from "../../action/note";
import { useEffect } from "react";

const Post =  (props) => {
  useEffect( () => {
     Posts().then( function (value) {
      console.log(value[0]);
      const firstPost = value[0]
      return (
        <div className="post">
          <div className="form__createPost">
            <h1> {firstPost.namePost} </h1>
            <div className="form__inputTextArea">
              <h1 className="input__text"> {firstPost.textPost} </h1>
            </div>
            <div className="input__dateAuthor">
              <h1 className="date"> {firstPost.dateTime} </h1>
              <h1 className="name"> {firstPost.name} </h1>
            </div>
          </div>
        </div>
      );
    }, []);
  });
};
export default Post;
