import React, { useState } from "react";
import { useSelector } from "react-redux";

const FormPost = () => {
  const [namePost, setNamePost] = useState("");
  const [textPost, setTextPost] = useState("");
  const datePost = useState(Date().slice(0,24));

  const currentUser = useSelector((state) => state.user.currentUser.email);
  const str_user = String(currentUser);
  const name = str_user.split("@")[0];

  return (
    <div>
      <form action="">
        <div className="input__name">
          <input
            value={namePost}
            setValue={setNamePost}
            type="text"
            placeholder="Name post"
          />
        </div>
        <div className="input__text">
          <textarea
            required = "true"
            rows="4" 
            cols="100"
            placeholder="Text post"
            maxlength = "5000"
          />
        </div>
        <div className="input__dateAuthor">
          <h1>{datePost}</h1>
          <h1> {name} </h1>
        </div>  
      </form>
    </div>
  );
};

export default FormPost;
