import axios from 'axios'



export const createPost = async (namePost, textPost, dateTime, name) => {
    // console.log(namePost, textPost, dateTime,name )
    try {
        const response = await axios.post(`http://localhost:5000/api/notes/create-post`, {
            namePost,
            textPost,
            dateTime,
            name
        })
        
        // console.log(response)
    } catch (e) {
        console.log(e)
    }

}

export const posts =  () => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://localhost:5000/api/notes/posts`,
                {headers:{Authorization:`Bearer ${localStorage.getItem('token')}`}}
            )
            dispatch(setUser(response.data.user))
            localStorage.setItem('token', response.data.token)
        } catch (e) {
            // console.log(e.response.data)
            localStorage.removeItem('token')
        }
    }
}
